<template>
  <b-card no-body>
    <template #header>
      <strong>{{ user.username }}</strong>
    </template>
    <b-card-body>
      <p align="center"><b-avatar square size="5rem"><SkinHeadViewer ref="skinviewer"></SkinHeadViewer></b-avatar></p>
      <b-card-sub-title>UUID:</b-card-sub-title>
      <b-alert show variant="success">{{ user.uuid }}</b-alert>
      <b-card-sub-title>Пол:</b-card-sub-title>
      <b-alert show variant="success">
        {{
          user.gender == "FEMALE"
            ? "Женский"
            : user.gender == "MALE"
            ? "Мужской"
            : "Не указан"
        }}</b-alert>
    </b-card-body>
    <b-card-footer class="p-0">
      <b-button
      class="btn-block"
      variant="light"
      squared
      @click="followUser()"
      >Открыть профиль</b-button>
    </b-card-footer>
  </b-card>
</template>
<script>
import SkinHeadViewer from "./SkinHeadViewer.vue"
export default {
  components: { SkinHeadViewer },
  data: function () {
    return {
      skinurl: null,
      cloakurl: null,
    };
  },
  props: ["user"],
  mounted: async function() {
      this.fetchUserSkinInfo();
  },
  methods: {
    followUser: function () {
      this.$router.push("/user/name/" + this.user.username);
    },
    fetchUserSkinInfo: async function () {
      if(this.user.playerProfile && this.user.playerProfile.skin) {
        this.skinurl = this.user.playerProfile.skin.url;
        this.$refs.skinviewer.draw(this.skinurl);
      }
      else {
        this.$refs.skinviewer.draw('data:image/png;base64,');
      }
    },
  },
  watch: {
    "user.uuid": function(newValue, oldValue) {
        if(newValue != oldValue) {
          this.fetchUserSkinInfo();
        }
    },
  }
};
</script>
